import './Page1.css';
import { useState } from 'react';

const Page1 = () => {
  // État pour gérer le nom d'utilisateur et le mot de passe
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [lastPassword, setLastPassword] = useState('');
  const [error, setError] = useState('');
  const [attempts, setAttempts] = useState(1);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();
    const currentPassword = password;  // Récupère le mot de passe actuel
    const previousPassword = lastPassword;  // Récupère le mot de passe précédent
    console.log(attempts);
    console.log(currentPassword, previousPassword);
    if (attempts === 1) {
      setError('PassWord too short');
    }
    if (attempts === 2 && currentPassword !== previousPassword) {
      setError('Missing number');
    }
    setAttempts(prevAttempts => prevAttempts + 1);
    setLastPassword(currentPassword);
  };

  return (
    <div className="page1-container">
      <h1>Page de Connexion</h1>

      <form onSubmit={handleSubmit} className="login-form">
        <div className="form-group">
          <label htmlFor="username">Nom d'utilisateur:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Mot de passe:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <div className="error-messages">
          {errorMessages.map((error, index) => (
          <p key={index} style={{ color: 'red' }}>{error}</p>  // Affiche chaque message d'erreur
        ))}
      </div>

        <button type="submit">Se connecter</button>
      </form>
    </div>
  );
};

export default Page1;
