import './Page1.css';
import { useState } from 'react';

const Page1 = () => {
  // État pour gérer le nom d'utilisateur et le mot de passe
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [attempts, setAttempts] = useState(0);  // Nombre de tentatives
  const [lastPassword, setLastPassword] = useState('');  // Dernier mot de passe
  const [passwordChanged, setPasswordChanged] = useState(false);

  // Fonction pour gérer la soumission du formulaire
  const handleSubmit = (event) => {
    event.preventDefault();

    setAttempts(prevAttempts => prevAttempts + 1);

  // Vérifier si le mot de passe a changé
  if (password !== lastPassword && attempts > 1) {
    setPasswordChanged(true);
    setLastPassword(password);
    setPasswordChanged
  } else {
    setPasswordChanged(false);
  }
  console.log('Tentatives de connexion:', attempts + 1);
  console.log('lastPassword', lastPassword);
  console.log('Mot de passe changé:', passwordChanged);
  console.log('password', password);
  };

  return (
    <div className="page1-container">
      <h1>Page de Connexion</h1>

      <form onSubmit={handleSubmit} className="login-form">
        <div className="form-group">
          <label htmlFor="username">Nom d'utilisateur:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Mot de passe:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        {error && <p className="error-message">{error}</p>}

        <button type="submit">Se connecter</button>
      </form>
    </div>
  );
};

export default Page1;
