import './Page1.css';
import { useState } from 'react';

const Page1 = () => {
  // État pour gérer le nom d'utilisateur et le mot de passe
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [lastPassword, setLastPassword] = useState('');
  const [error, setError] = useState('');
  const [attempts, setAttempts] = useState(1);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [errorMessages, setErrorMessages] = useState([]);

  const handleSubmit = (event) => {
    event.preventDefault();
    const currentPassword = password;  // Récupère le mot de passe actuel
    const previousPassword = lastPassword;  // Récupère le mot de passe précédent
    let newErrorMessages = [...errorMessages];
    if (currentPassword !== previousPassword) {
      if (attempts === 1) {
        newErrorMessages.unshift('Too Short');
      }
      if (attempts === 2) {
        newErrorMessages.unshift('Missing Number');
      }
      if (attempts === 3) {
        newErrorMessages.unshift('Missing Comma');
      }
      if (attempts === 4) {
        newErrorMessages.unshift('Missing point');
      }
      if (attempts === 5) {
        newErrorMessages.unshift('Missing Uppercase Letter');
      }
      if (attempts === 6) {
        newErrorMessages.unshift('Missing Lowercase Letter');
      }
      if (attempts === 7) {
        newErrorMessages.unshift('Missing Special Character');
      }
      if (attempts === 8) {
        newErrorMessages.unshift('Too long');
      }
      if (attempts === 9) {
        newErrorMessages.unshift('Weak Password');
      }
      if (attempts === 10) {
        newErrorMessages.unshift('Password Reuse Detected');
      }
      if (attempts === 11) {
        newErrorMessages.unshift('Common Password');
      }
      if (attempts === 12) {
        newErrorMessages.unshift('Too easy to guess');
      }
      if (attempts === 13) {
        newErrorMessages.unshift('write some more random letter');
      }
      if (attempts === 14) {
        newErrorMessages.unshift('and some number too');
      }
      if (attempts === 15) {
        newErrorMessages.unshift('Password Too Common');
      }
      if (attempts === 16) {
        newErrorMessages.unshift('Type Virgile>>');
      }
      if (attempts === 17) {
        setIsLoggedIn(true);
        console.log(isLoggedIn);
      }
      setAttempts(prevAttempts => prevAttempts + 1);  // Met à jour le compteur de tentatives
    }
    setErrorMessages(newErrorMessages);
    setLastPassword(currentPassword);
  };

  return (
    <div className="page1-container">
      <h1>Page de Connexion</h1>

      <form onSubmit={handleSubmit} className="login-form">
        <div className="form-group">
          <label htmlFor="username">Nom d'utilisateur:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Mot de passe:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <div className="error-messages">
          {errorMessages.map((error, index) => (
            <p key={index} style={{ color: 'red' }}>{error}</p>  // Affiche chaque message d'erreur
          ))}
        </div>

        <button type="submit">Se connecter</button>
      </form>
    </div>
  );
};

export default Page1;
